const int N = 3200;
bool vis[N][N];
int dp[N][N];
void gao()
{
    queue<int> Q;
    Q.push(0);
    Q.push(0);
    for(int i=0; i<N; ++i)
    {
        for(int j=0; j<N; ++j)
        {
            dp[i][j]=9999;
            vis[i][j]=0;
        }
    }
    dp[0][0]=0;
    while(!Q.empty())
    {
        int n = Q.front();
        Q.pop();
        int k = Q.front();
        Q.pop();
        int nn = n+1;
        int nk = k;
        vis[nn][nk]=false;
        if(dp[nn][nk]>dp[n][k]+1)
        {
            dp[nn][nk]=dp[n][k]+1;
            if(!vis[nn][nk])
            {
                Q.push(nn);
                Q.push(nk);
            }
        }
        nn = n-1;
        nk = k;
        if(dp[nn][nk]>dp[n][k]+1)
        {
            dp[nn][nk]=dp[n][k]+1;
            if(!vis[nn][nk])
            {
                Q.push(nn);
                Q.push(nk);
            }
        }
        nn = n;
        nk = n;
        if(dp[nn][nk]>dp[n][k]+1)
        {
            dp[nn][nk]=dp[n][k]+1;
            if(!vis[nn][nk])
            {
                Q.push(nn);
                Q.push(nk);
            }
        }
        nn = n+k;
        nk = k;
        if(nn>N)continue;
        if(dp[nn][nk]>dp[n][k]+1)
        {
            dp[nn][nk]=dp[n][k]+1;
            if(!vis[nn][nk])
            {
                Q.push(nn);
                Q.push(nk);
            }
        }
    }
}
